import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import Skeleton from 'react-loading-skeleton';
import { useDispatch } from 'react-redux';
import { addCart } from '../redux/action';
import { Footer, Navbar } from '../components';

const Product = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);

  const dispatch = useDispatch();

  const addProduct = (product) => {
    dispatch(addCart(product));
  };

  // Sample local product data
  const localProductsData = [
    { id: 1, title: 'Product 1', price: 29.99, image: 'path/to/image1.jpg', description: 'Description for product 1' },
    { id: 2, title: 'Product 2', price: 19.99, image: 'path/to/image2.jpg', description: 'Description for product 2' },

  ];

  useEffect(() => {
    setTimeout(() => {
      setProducts(localProductsData);
      setLoading(false);
    }, 1000); // Simulate loading delay (e.g., 1 second)
  }, []);

  const Loading = () => {
    return (
      <div className="container my-5 py-3">
        <div className="row">
          {[...Array(4)].map((_, index) => (
            <div className="col-md-3 col-sm-6 mb-4" key={index}>
              <Skeleton height={250} width={200} />
              <Skeleton height={20} width={150} />
              <Skeleton height={50} />
            </div>
          ))}
        </div>
      </div>
    );
  };

  const ShowProducts = () => {
    return (
      <div className="container my-5 py-3">
        <div className="row">
          {products.map((product) => (
            <div key={product.id} className="col-md-3 col-sm-6 mb-4">
              <div className="card h-100">
                <img src={product.image} className="card-img-top" alt={product.title} />
                <div className="card-body">
                  <h5 className="card-title">{product.title}</h5>
                  <p className="card-text">${product.price}</p>
                  <p className="card-text">{product.description}</p>
                  <button className="btn btn-dark" onClick={() => addProduct(product)}>
                    Add to Cart
                  </button>
                  <Link to={`/product/${product.id}`} className="btn btn-outline-dark mx-2">
                    View Details
                  </Link>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    );
  };

  return (
    <>
      <Navbar />
      <div className="container">
        <div className="row">
          {loading ? <Loading /> : <ShowProducts />}
        </div>
      </div>
      <Footer />
    </>
  );
};

export default Product;
