import React, { useEffect, useState } from "react";
import Skeleton from "react-loading-skeleton";
import { Link, useParams } from "react-router-dom";
import Marquee from "react-fast-marquee";
import { useDispatch } from "react-redux";
import { addCart } from "../redux/action";

import { Footer, Navbar } from "../components";

const Product = () => {
  const { id } = useParams();
  const [product, setProduct] = useState([]);
  const [similarProducts, setSimilarProducts] = useState([]);
  const [loading, setLoading] = useState(false);
  const [loading2, setLoading2] = useState(false);

  const dispatch = useDispatch();

  const addProduct = (product) => {
    dispatch(addCart(product));
  };

  useEffect(() => {
    setLoading(true);
    setLoading2(true);

    // Use local data instead of fetching from the API
    const productsData = [
      {
        id: 1,
        title: "КЕҢЖЕЙДЕ MDRN IDENTITY",
        price: 26,
        image: "../assets/image1.jpeg",
        description: "кеңжейде. кез келген мезгілге арналған ыңғайлы киім. топтамада базалық және принтті үлгілер бар. qr-дың фирмалық үлгісінде жасалған.",
        category: "КЕҢЖЕЙДЕ",
        rating: { rate: 4.5 },
      },
      {
        id: 2,
        title: "КЕҢЖЕЙДЕ MDRN IDENTITY",
        price: 29.99,
        image: "../assets/image2.jpeg",
        description: "кеңжейде. кез келген мезгілге арналған ыңғайлы киім. топтамада базалық және принтті үлгілер бар. qr-дың фирмалық үлгісінде жасалған.",
        category: "category1",
        rating: { rate: 4.5 },
      },
      {
        id: 3,
        title: "КЕҢЖЕЙДЕ MDRN IDENTITY",
        price: 29.99,
        image: "../assets/image3.jpeg",
        description: "кеңжейде. кез келген мезгілге арналған ыңғайлы киім. топтамада базалық және принтті үлгілер бар. qr-дың фирмалық үлгісінде жасалған.",
        category: "category1",
        rating: { rate: 4.5 },
      },
      {
        id: 4,
        title: "КЕҢЖЕЙДЕ MDRN IDENTITY",
